function loadOneTrustScript() {
    var link = document.createElement('link');
    link.type = 'text/css';
    link.href = 'https://cdn.cookielaw.org/skins/4.6.0/default_flat_bottom_two_button_black/v2/css/optanon.css';
    link.rel = 'stylesheet';
    document.getElementsByTagName('head')[0].appendChild(link);    
    var script = document.createElement("script");
    script.type = "text/javascript";
    script.src = "https://cdn.cookielaw.org/langswitch/d3e8af67-80f4-4e15-bf74-55c910c7611a.js";
    script.charset = "UTF-8";
    script.setAttribute("data-document-language", "true");
    document.getElementsByTagName("head")[0].appendChild(script);
    window.addEventListener("consent.onetrust", function() {
        function setActiveGroups() {
            var groups = OnetrustActiveGroups;
            var group1 = /,1,/;
            var group2 = /,2,/;
            var group3 = /,3,/;
            var group4 = /,4,/;
            var HPeucks = "";

            if (groups.match(group1)) {
                HPeucks += '1';
            }
            if (groups.match(group2)) {
                HPeucks += '1';
            } else {
                HPeucks += '0';
            }
            if (groups.match(group3)) {
                HPeucks += '1';
            } else {
                HPeucks += '0';
            }
            if (groups.match(group4)) {
                HPeucks += '1';
            } else {
                HPeucks += '0';
            }

            var t = new Date;
            t.setTime(t.getTime() + 31536e6);

            var i = "hpeuck_prefs=" + HPeucks + "; expires=" + t.toUTCString() + "; domain=" + location.host + "; path=/";
            document.cookie = i;
        }

        setActiveGroups();

        Optanon.OnConsentChanged(function() {
            setActiveGroups();
        });
    });
}

loadOneTrustScript();

set_getHP_cookie = function() {

    //setTimeout(function(){Optanon.LoadBanner();},1000);

    _getHP_cookie = function(e) {
            var a = document.cookie.split(";");
            if (a)
                for (var c = 0; c < a.length; c++)
                    if (a[c].indexOf(e) >= 0)
                        return a[c].trim()
        },
        _getHP_cookieSegment = function(e, a) {
            for (var c = 0; c < e.length; c++)
                if (0 == (_a = e[c].trim()).indexOf(a))
                    return _a;
            return null
        },
        _setHPeuck_prefs = function() {
            if (_ocCookie = _getHP_cookie("OptanonConsent"),
                _ocCookie) {
                console.log("OptanonConsentfound");
                console.log("OC Cookie: " + _ocCookie);
                var e = _ocCookie.split("&");
                if (e) {
                    var a = decodeURIComponent(_getHP_cookieSegment(e, "groups")),
                        c = decodeURIComponent(_getHP_cookieSegment(e, "datestamp"));
                    if (a) {
                        var n = null,
                            f = c.split("=");
                        f && f.length > 1 && (n = new Date(f[1].replace(/\+/g, " ")));
                        var t = new Date;
                        if (!n || n.getDate() == t.getDate() && n.getHours() == t.getHours() && n.getMinutes() == t.getMinutes()) {
                            console.log("OC Cookie: Consent Dates Have Changed");
                            var b = a.split("=");
                            if (b.length > 1) {
                                _nv = b[1].split(","),
                                    console.log("OptanonConsent: " + _nv);
                                for (var d = ["1", "1", "1", "1"], o = 0; o < _nv.length && o < 4; o++) {
                                    var r = parseInt(_nv[o].split(":")[0]),
                                        s = _nv[o].split(":")[1];
                                    r > 0 && r < 5 && (d[r - 1] = s)
                                }
                                t.setTime(t.getTime() + 31536e6);
                                console.log(location.host);
                                var i = "hpeuck_prefs=" + d.toString().replace(/,/g, "") + "; expires=" + t.toUTCString() + "; domain=" + location.host + "; path=/";

                                document.cookie = i;
                                console.log("HP euck_prefs: " + i);
                            }
                        }
                    }
                }
            }
        }
        _setHPeuck_prefs();
}
